import React, { useContext, useState } from 'react';
import './ProductDisplay.css';
import { ShopContext } from '../../Context/ShopContext';

export const ProductDisplay = (props) => {
    const { product } = props;
    const { addToCart } = useContext(ShopContext);

    // State for managing the popup and selected size
    const [isPopupVisible, setPopupVisible] = useState(false);
    const [selectedSize, setSelectedSize] = useState(null);

    // Function to handle size selection
    const handleSizeSelect = (size) => {
        setSelectedSize(size);
    };

    // Function to handle adding product to the cart
    const handleAddToCart = () => {
        if (selectedSize) {
            // Send product and selected size to the cart
            addToCart(product.id, selectedSize);
            setPopupVisible(false); // Hide the popup after adding to cart
        } else {
            alert('กรุณาเลือกไซต์ก่อน');
        }
    };

    return (
        <div className='productdisplay'>
            <div className="productdisplay-left">
                <div className="productdisplay-img-list">
                    <img src={product.image} alt="" />
                    <img src={product.image} alt="" />
                    <img src={product.image} alt="" />
                    <img src={product.image} alt="" />
                </div>
                <div className="productdisplay-image">
                    <img className='productdisplay-main-img' src={product.image} alt="" />
                </div>
            </div>
            <div className="productdisplay-right">
                <h1>{product.name}</h1>
                <div className="productdisplay-right-prices">
                    <div className="productdisplay-right-prices-old">${product.old_price}</div>
                    <div className="productdisplay-right-prices-new">${product.new_price}<span> บาท</span></div>
                </div>
                <div className="productdisplay-right-description">
                    {product.description}
                </div>
                <div className="productdisplay-right-size">
                    <h1>เลือกไซต์</h1>
                    <div className="productdisplay-right-sizes">
                        <div onClick={() => handleSizeSelect('S')}>S</div>
                        <div onClick={() => handleSizeSelect('M')}>M</div>
                        <div onClick={() => handleSizeSelect('L')}>L</div>
                        <div onClick={() => handleSizeSelect('XL')}>XL</div>
                        <div onClick={() => handleSizeSelect('XXL')}>XXL</div>
                    </div>
                </div>
                <button className="addtocart" onClick={() => setPopupVisible(true)}>ADD TO CART</button>
            </div>

            {/* Popup for selecting size */}
            {isPopupVisible && (
                <div className="popup">
                    <div className="popup-content">
                        <h2>เลือกไซต์</h2>
                        <div className="popup-sizes">
                            <div onClick={() => handleSizeSelect('S')}>S</div>
                            <div onClick={() => handleSizeSelect('M')}>M</div>
                            <div onClick={() => handleSizeSelect('L')}>L</div>
                            <div onClick={() => handleSizeSelect('XL')}>XL</div>
                            <div onClick={() => handleSizeSelect('XXL')}>XXL</div>
                        </div>
                        <button onClick={handleAddToCart}>เพิ่มลงในตะกร้า</button>
                        <button onClick={() => setPopupVisible(false)}>ปิด</button>
                    </div>
                </div>
            )}
        </div>
    );
};
