import React, { useState, useContext } from 'react';
import './ProductDisplay.css';
import { ShopContext } from '../../Context/ShopContext';

export const ProductDisplay = (props) => {
    const { product } = props;
    const { addToCart } = useContext(ShopContext);

    // State to control the visibility of the popup and the selected size
    const [isPopupOpen, setIsPopupOpen] = useState(false);
    const [selectedSize, setSelectedSize] = useState('');

    // Handle size selection
    const handleSizeSelect = (size) => {
        setSelectedSize(size);  // Update the selected size state
    };

    // Handle the 'Add to Cart' button click (with selected size)
    const handleAddToCart = () => {
        if (selectedSize) {
            // Close the popup and add to cart with selected size
            setIsPopupOpen(false);
            addToCart(product.id, selectedSize); // Make sure product.id and selectedSize are sent
        } else {
            alert('Please select a size');
        }
    };

    return (
        <div className="productdisplay">
            <div className="productdisplay-left">
                <div className="productdisplay-img-list">
                    <img src={product.image} alt="product" />
                    <img src={product.image} alt="product" />
                    <img src={product.image} alt="product" />
                    <img src={product.image} alt="product" />
                </div>
                <div className="productdisplay-image">
                    <img className="productdisplay-main-img" src={product.image} alt="product" />
                </div>
            </div>
            <div className="productdisplay-right">
                <h1>{product.name}</h1>
                <div className="productdisplay-right-prices">
                    <div className="productdisplay-right-prices-old">${product.old_price}</div>
                    <div className="productdisplay-right-prices-new">${product.new_price} บาท</div>
                </div>
                <div className="productdisplay-right-description">
                    {product.description}
                </div>
                <div className="productdisplay-right-size">
                    <h1>เลือกไซต์</h1>
                    <div className="productdisplay-right-sizes">
                        {['S', 'M', 'L', 'XL', 'XXL'].map((size) => (
                            <div
                                key={size}
                                className={size === selectedSize ? 'size-selected' : ''}
                                onClick={() => handleSizeSelect(size)}
                            >
                                {size}
                            </div>
                        ))}
                    </div>
                </div>
                <button className="addtocart" onClick={() => setIsPopupOpen(true)}>ADD TO CART</button>
            </div>

            {/* Popup for size selection */}
            {isPopupOpen && (
                <div className="size-popup">
                    <h2>Select Size</h2>
                    <div className="productdisplay-right-sizes">
                        {['S', 'M', 'L', 'XL', 'XXL'].map((size) => (
                            <div
                                key={size}
                                className={size === selectedSize ? 'size-selected' : ''}
                                onClick={() => handleSizeSelect(size)}
                            >
                                {size}
                            </div>
                        ))}
                    </div>
                    <button className="addtocart-popup" onClick={handleAddToCart}>Add to Cart</button>
                    <button className="close-popup" onClick={() => setIsPopupOpen(false)}>Cancel</button>
                </div>
            )}
        </div>
    );
};
