import React, { useState } from 'react'
import './CSS/LoginSignUp.css'

export const LoginSignup = () => {

  const [state, setState] = useState("Login")

  const login = async () => {
    console.log("Login Fuction Executed")
  }
  const signup = async () => {
    console.log("Sign UP Fuction Executed")
  }
  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <div className="loginsignup-fields">
          {state === "Sign Up" ? <input type="text" placeholder='รหัสนักศึกษา' /> : <></>}
          <input type="email" placeholder='อีเมลนักศึกษา' />
          <input type="password" placeholder='รหัสผ่าน' />
        </div>
        <button onClick={() => { state === "Login" ? login() : signup() }}><span>ลงชื่อเข้าใช้</span></button>
        {state === "Sign Up" ? <p className='loginsignup-login'>Already have an account? <span onClick={() => { setState("Login") }}>Login here</span></p>
          : <p className='loginsignup-login'>Create an account? <span onClick={() => { setState("Sign Up") }}>Click here</span></p>}

        <div className="loginsignup-agree">
          <input type="checkbox" name='' id='' />
          <p>By continuing, i agree to the terms of use & privacy policy.</p>
        </div>
      </div>
    </div>
  )
}

export default LoginSignup
